function signInInit(){$("input").on("keyup",function(a){if(a.which==13){a.preventDefault();signIn()}})}function signIn(){var b=$("#signin-email").val();var a=$("#signin-password").val();if(b==""){signInMessage("email")}else{if(a==""){signInMessage("password")}else{$.ajax({url:"classes/SignIn.php",type:"GET",data:{signin:true,email:b,password:a},success:function(c){if(c==="yes"){signInMessage("goodpass");window.location.href="index.php"}else{if(c==="no"){signInMessage("badpass")}}}})}}}function signInMessage(a){var b=$("#signin-error");b.hide();b.removeClass("bs-callout-warning");b.removeClass("bs-callout-danger");b.removeClass("bs-callout-info");switch(a){case"email":b.addClass("bs-callout-warning");b.find("p").text("Введите email.");break;case"password":b.addClass("bs-callout-warning");b.find("p").text("Введите пароль.");break;case"badpass":b.addClass("bs-callout-danger");b.find("p").text("Неверное имя пользователя или пароль.");break;case"goodpass":b.addClass("bs-callout-info");b.find("p").text("Пожалуйста, подождите...");break}b.show()}function signOut(){$.ajax({url:"classes/SignOut.php",type:"GET",success:function(a){location.reload()}})};