function signInInit(){$("input").on("keyup",function(a){if(a.which==13){a.preventDefault();signIn()}})}function signIn(){var c=$("#signin-error");var b=$("#signin-email").val();var a=$("#signin-password").val();if(b==""){c.removeClass("bs-callout-danger");c.addClass("bs-callout-warning");c.find("p").text("Введите email.");c.show()}else{if(a==""){c.removeClass("bs-callout-danger");c.addClass("bs-callout-warning");c.find("p").text("Введите пароль.");c.show()}else{$.ajax({url:"classes/SignIn.php",type:"GET",data:{signin:true,email:b,password:a},success:function(d){if(d==="yes"){$("#signin-error").hide();window.location.href="index.php"}else{if(d==="no"){c.removeClass("bs-callout-warning");c.addClass("bs-callout-danger");c.find("p").text("Неверное имя пользователя или пароль.");$("#signin-error").show()}}}})}}}function signOut(){$.ajax({url:"classes/SignOut.php",type:"GET",success:function(a){location.reload()}})};