function signInInit(){$("input").on("keyup",function(a){if(a.which==13){a.preventDefault();signIn()}});$("#signin-form").submit(function(a){a.preventDefault();signIn();return false})}function signIn(){var c=$("#signin-btn");var b=$("#signin-email").val();var a=$("#signin-password").val();c.prop("disabled",true);if(b==""){signInMessage("email");c.prop("disabled",false)}else{if(a==""){signInMessage("password");c.prop("disabled",false)}else{$.ajax({url:"actions/SignIn.php",type:"GET",data:{signin:true,email:b,password:a},success:function(d){if(d==="yes"){signInMessage("goodpass");window.location.href="index.php"}else{if(d==="no"){signInMessage("badpass");c.prop("disabled",false)}}}})}}}function signInMessage(a){var b=$("#signin-error");b.hide();b.removeClass("bs-callout-warning");b.removeClass("bs-callout-danger");b.removeClass("bs-callout-info");switch(a){case"email":b.addClass("bs-callout-warning");b.find("p").text("Введите email.");break;case"password":b.addClass("bs-callout-warning");b.find("p").text("Введите пароль.");break;case"badpass":b.addClass("bs-callout-danger");b.find("p").text("Неверное имя пользователя или пароль.");break;case"goodpass":b.addClass("bs-callout-info");b.find("p").text("Пожалуйста, подождите...");break}b.show()}function signOut(){$.ajax({url:"actions/SignOut.php",type:"GET",success:function(a){location.reload()}})};